        } else {
            int newY = mouseRootY;
            int newHeight = y+h - mouseRootY;
            if (newHeight < _topBorder+_bottomBorder) {
                newHeight = _topBorder+_bottomBorder;
            }
            [x11dict setValue:nsfmt(@"%d %d", x, newY) forKey:@"moveWindow"];
            [x11dict setValue:nsfmt(@"%d %d", w, newHeight) forKey:@"resizeWindow"];
            return;
        }
    }
    if (_buttonDown == '1') {
        int newX = mouseRootX;
        int newWidth = x+w - mouseRootX;
        int newHeight = mouseY;
        if (newWidth < _leftBorder+_rightBorder) {
            newWidth = _leftBorder+_rightBorder;
        }
        if (newHeight < _topBorder+_bottomBorder) {
            newHeight = _topBorder+_bottomBorder;
        }
        [x11dict setValue:nsfmt(@"%d %d", newX, y) forKey:@"moveWindow"];
        [x11dict setValue:nsfmt(@"%d %d", newWidth, newHeight) forKey:@"resizeWindow"];
        return;
    }
    if (_buttonDown == '3') {
        int newWidth = mouseX;
        int newHeight = mouseY;
        if (newWidth < _leftBorder+_rightBorder) {
            newWidth = _leftBorder+_rightBorder;
        }
        if (newHeight < _topBorder+_bottomBorder) {
            newHeight = _topBorder+_bottomBorder;
        }
        [x11dict setValue:nsfmt(@"%d %d", newWidth, newHeight) forKey:@"resizeWindow"];
        return;
    }
    if (_buttonDown == '7') {
        int newX = mouseRootX;
        int newY = mouseRootY;
        int newWidth = x+w - mouseRootX;
        int newHeight = y+h - mouseRootY;
        if (newWidth < _leftBorder+_rightBorder) {
            newWidth = _leftBorder+_rightBorder;
        }
        if (newHeight < _topBorder+_bottomBorder) {
            newHeight = _topBorder+_bottomBorder;
        }
        [x11dict setValue:nsfmt(@"%d %d", newX, newY) forKey:@"moveWindow"];
        [x11dict setValue:nsfmt(@"%d %d", newWidth, newHeight) forKey:@"resizeWindow"];
        return;
    }
    if (_buttonDown == '9') {
        int newY = mouseRootY;
        int newWidth = mouseX;
        int newHeight = y+h - mouseRootY;
        if (newWidth < _leftBorder+_rightBorder) {
            newWidth = _leftBorder+_rightBorder;
        }
        if (newHeight < _topBorder+_bottomBorder) {
            newHeight = _topBorder+_bottomBorder;
        }
        [x11dict setValue:nsfmt(@"%d %d", x, newY) forKey:@"moveWindow"];
        [x11dict setValue:nsfmt(@"%d %d", newWidth, newHeight) forKey:@"resizeWindow"];
        return;
    }
    [windowManager setX11Cursor:'5'];
    _buttonHover = 0;
}
- (void)handleMouseUp:(id)event
{
    id dict = [event valueForKey:@"x11dict"];
    if ((_buttonDown == 'r') && (_buttonHover == 'r')) {
        [dict x11CloseWindow];
    }
    if ((_buttonDown == 'y') && (_buttonHover == 'y')) {
        id windowManager = [event valueForKey:@"windowManager"];
        [windowManager lowerObjectWindow:dict];
    }
    if ((_buttonDown == 'g') && (_buttonHover == 'g')) {
        [dict x11ToggleMaximizeWindow];
    }
    if ((_buttonDown == 'T') && (_buttonHover == 'T')) {
    }
    if (_buttonDown == 't') {
        /* this was added for Wine */
        [dict x11MoveChildWindowBackAndForthForWine];
    }

    _buttonDown = 0;
}
- (void)drawButtonInBitmap:(id)bitmap :(unsigned char *)left :(unsigned char *)middle :(unsigned char *)right
{
    if (!left || !middle || !right) {
        return;
    }

    unsigned char *pixelBytes = [bitmap pixelBytes];
    if (!pixelBytes) {
        return;
    }
    int bitmapWidth = [bitmap bitmapWidth];
    int bitmapHeight = [bitmap bitmapHeight];
    int bitmapBytesPerRow = bitmapWidth*4;

    if (bitmapWidth < (left[1]+middle[1]+right[1])*_pixelScaling) {
        return;
    }
    if (bitmapHeight < left[3]*_pixelScaling) {
        return;
    }
    if (bitmapHeight < middle[3]*_pixelScaling) {
        return;
    }
    if (bitmapHeight < right[3]*_pixelScaling) {
        return;
    }

    unsigned char *rgb = left;
    
    int w1;
    int w2;
    int width;
    int height;
    int bytes_per_row;

    width = rgb[1];
    w1 = width*_pixelScaling;
    height = rgb[3];
    bytes_per_row = width*3;
    for (int y=0; y<height*_pixelScaling; y++) {
        for (int x=0; x<width*_pixelScaling; x++) {
            int i = y*bitmapBytesPerRow + x*4;
            int j = (y/_pixelScaling)*bytes_per_row + (x/_pixelScaling)*3;
            pixelBytes[i] = rgb[4+j+2];
            pixelBytes[i+1] = rgb[4+j+1];
            pixelBytes[i+2] = rgb[4+j+0];
            pixelBytes[i+3] = 255;
        }
    }

    rgb = right;

    width = rgb[1];
    w2 = width*_pixelScaling;
    height = rgb[3];
    bytes_per_row = width*3;
int offset = (bitmapWidth - w2) * 4;
    for (int y=0; y<height*_pixelScaling; y++) {
        for (int x=0; x<width*_pixelScaling; x++) {
            int i = y*bitmapBytesPerRow + x*4 + offset;
            int j = (y/_pixelScaling)*bytes_per_row + (x/_pixelScaling)*3;
            pixelBytes[i] = rgb[4+j+2];
            pixelBytes[i+1] = rgb[4+j+1];
            pixelBytes[i+2] = rgb[4+j+0];
            pixelBytes[i+3] = 255;
        }
    }

    rgb = middle;

    width = rgb[1];
    height = rgb[3];
    bytes_per_row = width*3;
    for (int y=0; y<height*_pixelScaling; y++) {
        int j = (y/_pixelScaling)*bytes_per_row;
        for (int x=w1; x<bitmapWidth-w2; x++) {
            int i = y*bitmapBytesPerRow + x*4;
            pixelBytes[i] = rgb[4+j+2];
            pixelBytes[i+1] = rgb[4+j+1];
            pixelBytes[i+2] = rgb[4+j+0];
            pixelBytes[i+3] = 255;
        }
    }
}
@end

